mapper.update:
update ord
set od_dt       = #{od_dt}
,   od_stat_cd  = #{od_stat_cd}
,   od_pd_qty   = #{od_pd_qty}
,   od_tot_qty  = #{od_tot_qty}
,   od_pay_amt  = #{od_pay_amt}
,   rtl_prc     = #{rtl_prc}
,   frst_reg_id = #{frst_reg_id}
,   last_mod_id = #{last_mod_id}
where od_id = #{od_id} and c_id = #{c_id} order by od_dt desc;
