mapper.update:
update ord_det
set   od_id           = #{od_id}
  ,   c_id            = #{c_id}
  ,   pd_id           = #{pd_id}
  ,   pd_clsf_cd      = #{pd_clsf_cd}
  ,   od_dt           = #{od_dt}
  ,   pd_name         = #{pd_name}
  ,   sls_prc         = #{sls_prc}
  ,   od_qty          = #{od_qty}
  ,   od_stat_cd      = #{od_stat_cd}
  ,   frst_reg_dt     = #{frst_reg_dt}
  ,   frst_reg_id     = #{frst_reg_id}
  ,   last_mod_dt     = #{last_mod_dt}
  ,   last_mod_id     = #{last_mod_id}
where od_det_seqnum   = #{od_det_seqnum}
order by od_id desc;
