mapper.update:
update prd
pd_name = #{pd_name}, mdl_name = #{mdl_name}, pd_ad_cmt = #{pd_ad_cmt}, pd_smr_dsc = #{pd_smr_dsc}, pd_det_dsc = #{pd_det_dsc}, min_od_qty = #{min_od_qty}, max_od_qty = #{max_od_qty}, pd_is_show = #{pd_is_show}, sls_strt_dt = #{sls_strt_dt}, hit_cnt = #{hit_cnt}, rvw_cnt = #{rvw_cnt}, wish_cnt = #{wish_cnt}, pd_chr_cd = #{pd_chr_cd}, sprt_type_cd = #{sprt_type_cd}, pd_type_cd = #{pd_type_cd}, pd_type_det_cd = #{pd_type_det_cd}, brd_cd = #{brd_cd}, pd_stat_hist_cd = #{pd_stat_hist_cd},
#{item}

update prd
set hit_cnt = hit_cnt + 1
where pd_id = #{pd_id}

mapper.select:
select count(*)
from prd
where pd_type_cd = #{pd_type_cd}
